    public static ArrayList<Film> getListData(){
        ArrayList<Film> list = new ArrayList<>();
        for (String[] aData : data) {
            Film film = new Film();
            film.setName(aData[0]);
            film.setFrom(aData[1]);
            film.setPhoto(aData[2]);
            list.add(film);
        }
        return list;
    }
